186    int init_mode;
187    int heuristic;
188    int num_nodes;
189    stbrp_node *active_head;
190    stbrp_node *free_head;
191    stbrp_node extra[2]; // we allocate two extra nodes so optimal user-node-count is 'width' not 'width+2'
192 };
