144     struct compute_sign
145     {
146         GLM_FUNC_QUALIFIER static vec<L, T, Q> call(vec<L, T, Q> const& x)
147         {
148             return vec<L, T, Q>(glm::lessThan(vec<L, T, Q>(0), x)) - vec<L, T, Q>(glm::lessThan(x, vec<L, T, Q>(0)));
149         }
