752     GLM_FUNC_QUALIFIER genType frexp(genType x, int& exp)
753     {
754         GLM_STATIC_ASSERT(std::numeric_limits<genType>::is_iec559, "'frexp' only accept floating-point inputs");
755 
756         return std::frexp(x, &exp);
757     }
