35 #define GLM_EXTRACT_WORDS(ix0,ix1,d)        \
36     do {                                    \
37         ieee_double_shape_type ew_u;        \
38         ew_u.value = (d);                   \
39         (ix0) = ew_u.parts.msw;             \
40         (ix1) = ew_u.parts.lsw;             \
41     } while (0)
