388       void
389       _M_put_node(_Node* __p)
390       {
391     typedef typename _Node_alloc_traits::pointer _Ptr;
392     auto __ptr = std::pointer_traits<_Ptr>::pointer_to(*__p);
393     _Node_alloc_traits::deallocate(_M_get_Node_allocator(), __ptr, 1);
394       }
