84   enum class copy_options : unsigned short {
85       none = 0,
86       skip_existing = 1, overwrite_existing = 2, update_existing = 4,
87       recursive = 8,
88       copy_symlinks = 16, skip_symlinks = 32,
89       directories_only = 64, create_symlinks = 128, create_hard_links = 256
90   };
