691 
701     fmtflags
702     setf(fmtflags __fmtfl, fmtflags __mask)
703     {
704       fmtflags __old = _M_flags;
705       _M_flags &= ~__mask;
706       _M_flags |= (__fmtfl & __mask);
707       return __old;
708     }
