723       using _Base::_M_get_Bit_allocator;
724 
725     public:
726 #if __cplusplus >= 201103L
727       vector() = default;
728 #else
729       vector() { }
730 #endif
731 
732       _GLIBCXX20_CONSTEXPR
733       explicit
734       vector(const allocator_type& __a)
