1383       _GLIBCXX20_CONSTEXPR
1384       void
1385       _M_fill_assign(size_t __n, bool __x)
1386       {
1387     if (__n > size())
1388       {
1389         _M_initialize_value(__x);
1390         insert(end(), __n - size(), __x);
1391       }
1392     else
1393       {
1394         _M_erase_at_end(begin() + __n);
1395         _M_initialize_value(__x);
1396       }
1397       }
