134     friend class map<_Key, _Tp, _Compare, _Alloc>;
135       protected:
136     _Compare comp;
137 
138     value_compare(_Compare __c)
139     : comp(__c) { }
140 
141       public:
142     bool operator()(const value_type& __x, const value_type& __y) const
143     { return comp(__x.first, __y.first); }
144       };
