446     path& _M_append(const string_type& __str)
447     {
448       if (!_M_pathname.empty() && !_S_is_dir_sep(_M_pathname.back())
449       && !__str.empty() && !_S_is_dir_sep(__str.front()))
450     _M_pathname += preferred_separator;
451       _M_pathname += __str;
452       _M_split_cmpts();
453       return *this;
454     }
