514     static bool _S_is_dir_sep(value_type __ch)
515     {
516 #ifdef _GLIBCXX_FILESYSTEM_IS_WINDOWS
517       return __ch == L'/' || __ch == preferred_separator;
518 #else
519       return __ch == '/';
520 #endif
521     }
