733   // specialize _Cvt for degenerate 'noconv' case
734   template<>
735     struct path::_Cvt<path::value_type>
736     {
737       template<typename _Iter>
738     static string_type
739     _S_convert(_Iter __first, _Iter __last)
740     { return string_type{__first, __last}; }
741     };
