743   template<typename _CharT>
744     struct path::_Cvt
745     {
746 #ifdef _GLIBCXX_FILESYSTEM_IS_WINDOWS
747 #ifdef _GLIBCXX_USE_CHAR8_T
748       static string_type
749       _S_wconvert(const char8_t* __f, const char8_t* __l, const char8_t*)
750       {
751     const char* __f2 = (const char*)__f;
752     const char* __l2 = (const char*)__l;
753     std::wstring __wstr;
754     std::codecvt_utf8_utf16<wchar_t> __wcvt;
755     if (__str_codecvt_in_all(__f2, __l2, __wstr, __wcvt))
756       return __wstr;
757       }
758 #endif
759 
760       static string_type
761       _S_wconvert(const char* __f, const char* __l, const char*)
762       {
763     using _Cvt = std::codecvt<wchar_t, char, mbstate_t>;
764     const auto& __cvt = std::use_facet<_Cvt>(std::locale{});
765     std::wstring __wstr;
766     if (__str_codecvt_in_all(__f, __l, __wstr, __cvt))
767         return __wstr;
768     _GLIBCXX_THROW_OR_ABORT(filesystem_error(
769           "Cannot convert character sequence",
770           std::make_error_code(errc::illegal_byte_sequence)));
771       }
772 
773       static string_type
774       _S_wconvert(const _CharT* __f, const _CharT* __l, const void*)
775       {
776     struct _UCvt : std::codecvt<_CharT, char, std::mbstate_t>
777     { } __cvt;
778     std::string __str;
779     if (__str_codecvt_out_all(__f, __l, __str, __cvt))
780       {
781         const char* __f2 = __str.data();
782         const char* __l2 = __f2 + __str.size();
783         std::codecvt_utf8_utf16<wchar_t> __wcvt;
784         std::wstring __wstr;
785         if (__str_codecvt_in_all(__f2, __l2, __wstr, __wcvt))
786           return __wstr;
787       }
788     _GLIBCXX_THROW_OR_ABORT(filesystem_error(
789           "Cannot convert character sequence",
790           std::make_error_code(errc::illegal_byte_sequence)));
791       }
792 
793       static string_type
794       _S_convert(const _CharT* __f, const _CharT* __l)
795       {
796     return _S_wconvert(__f, __l, (const _CharT*)nullptr);
797       }
798 #else
799       static string_type
800       _S_convert(const _CharT* __f, const _CharT* __l)
801       {
802 #ifdef _GLIBCXX_USE_CHAR8_T
803     if constexpr (is_same<_CharT, char8_t>::value)
804       return string_type(__f, __l);
805     else
806 #endif
807       {
808         struct _UCvt : std::codecvt<_CharT, char, std::mbstate_t>
809         { } __cvt;
810         std::string __str;
811         if (__str_codecvt_out_all(__f, __l, __str, __cvt))
812           return __str;
813         _GLIBCXX_THROW_OR_ABORT(filesystem_error(
814           "Cannot convert character sequence",
815           std::make_error_code(errc::illegal_byte_sequence)));
816       }
817       }
818 #endif
819 
820       static string_type
821       _S_convert(_CharT* __f, _CharT* __l)
822       {
823     return _S_convert(const_cast<const _CharT*>(__f),
824               const_cast<const _CharT*>(__l));
825       }
826 
827       template<typename _Iter>
828     static string_type
829     _S_convert(_Iter __first, _Iter __last)
830     {
831       const std::basic_string<_CharT> __str(__first, __last);
832       return _S_convert(__str.data(), __str.data() + __str.size());
833     }
834 
835       template<typename _Iter, typename _Cont>
836     static string_type
837     _S_convert(__gnu_cxx::__normal_iterator<_Iter, _Cont> __first,
838           __gnu_cxx::__normal_iterator<_Iter, _Cont> __last)
839     { return _S_convert(__first.base(), __last.base()); }
840     };
