844   class path::iterator
845   {
846   public:
847     using difference_type   = std::ptrdiff_t;
848     using value_type        = path;
849     using reference     = const path&;
850     using pointer       = const path*;
851     using iterator_category = std::bidirectional_iterator_tag;
852 
853     iterator() noexcept : _M_path(nullptr), _M_cur(), _M_at_end() { }
854 
855     iterator(const iterator&) = default;
856     iterator& operator=(const iterator&) = default;
857 
858     reference operator*() const noexcept;
859     pointer   operator->() const noexcept { return std::__addressof(**this); }
860 
861     iterator& operator++() noexcept;
862 
863     iterator  operator++(int) noexcept
864     { auto __tmp = *this; ++*this; return __tmp; }
865 
866     iterator& operator--() noexcept;
867 
868     iterator  operator--(int) noexcept
869     { auto __tmp = *this; --*this; return __tmp; }
870 
871     friend bool
872     operator==(const iterator& __lhs, const iterator& __rhs) noexcept
873     { return __lhs._M_equals(__rhs); }
874 
875     friend bool
876     operator!=(const iterator& __lhs, const iterator& __rhs) noexcept
877     { return !__lhs._M_equals(__rhs); }
878 
879   private:
880     friend class path;
881 
882     iterator(const path* __path, path::_List::const_iterator __iter) noexcept
883     : _M_path(__path), _M_cur(__iter), _M_at_end()
884     { }
885 
