1123   template<typename _CharT, typename _Traits, typename _Allocator>
1124     inline std::basic_string<_CharT, _Traits, _Allocator>
1125     path::generic_string(const _Allocator& __a) const
1126     {
1127 #ifdef _GLIBCXX_FILESYSTEM_IS_WINDOWS
1128       const _CharT __slash = is_same<_CharT, wchar_t>::value
1129     ? _CharT(L'/')
1130     : _CharT('/'); // Assume value is correct for the encoding.
1131 #else
1132       const _CharT __slash = _CharT('/');
1133 #endif
1134       basic_string<_CharT, _Traits, _Allocator> __str(__a);
1135       __str.reserve(_M_pathname.size());
1136       bool __add_slash = false;
1137       for (auto& __elem : *this)
1138     {
1139       if (__elem._M_type == _Type::_Root_dir)
1140         {
1141           __str += __slash;
1142           continue;
1143         }
1144       if (__add_slash)
1145         __str += __slash;
1146       __str += __elem.string<_CharT, _Traits, _Allocator>(__a);
1147       __add_slash = __elem._M_type == _Type::_Filename;
1148     }
1149       return __str;
1150     }
