1231   inline path::iterator
1232   path::begin() const noexcept
1233   {
1234     if (_M_type == _Type::_Multi)
1235       return iterator(this, _M_cmpts.begin());
1236     return iterator(this, false);
1237   }
1238 
1239   inline path::iterator
1240   path::end() const noexcept
1241   {
1242     if (_M_type == _Type::_Multi)
1243       return iterator(this, _M_cmpts.end());
1244     return iterator(this, true);
1245   }
1246 
1247   inline path::iterator&
1248   path::iterator::operator++() noexcept
1249   {
1250     __glibcxx_assert(_M_path != nullptr);
1251     if (_M_path->_M_type == _Type::_Multi)
1252       {
1253     __glibcxx_assert(_M_cur != _M_path->_M_cmpts.end());
1254     ++_M_cur;
1255       }
1256     else
1257       {
1258     __glibcxx_assert(!_M_at_end);
1259     _M_at_end = true;
1260       }
1261     return *this;
1262   }
1263 
1264   inline path::iterator&
1265   path::iterator::operator--() noexcept
1266   {
1267     __glibcxx_assert(_M_path != nullptr);
1268     if (_M_path->_M_type == _Type::_Multi)
1269       {
1270     __glibcxx_assert(_M_cur != _M_path->_M_cmpts.begin());
1271     --_M_cur;
1272       }
1273     else
1274       {
1275     __glibcxx_assert(_M_at_end);
1276     _M_at_end = false;
1277       }
1278     return *this;
1279   }
1280 
1281   inline path::iterator::reference
1282   path::iterator::operator*() const noexcept
1283   {
1284     __glibcxx_assert(_M_path != nullptr);
1285     if (_M_path->_M_type == _Type::_Multi)
1286       {
1287     __glibcxx_assert(_M_cur != _M_path->_M_cmpts.end());
1288     return *_M_cur;
1289       }
1290     return *_M_path;
1291   }
1292 
1293   inline bool
1294   path::iterator::_M_equals(iterator __rhs) const noexcept
1295   {
1296     if (_M_path != __rhs._M_path)
1297       return false;
1298     if (_M_path == nullptr)
1299       return true;
1300     if (_M_path->_M_type == path::_Type::_Multi)
1301       return _M_cur == __rhs._M_cur;
1302     return _M_at_end == __rhs._M_at_end;
1303   }
1304 
1305   // Define these now that path and path::iterator are complete.
1306   // They needs to consider the string_view(Range&&) constructor during
1307   // overload resolution, which depends on whether range<path> is satisfied,
1308   // which depends on whether path::iterator is complete.
1309   inline bool operator<(const path& __lhs, const path& __rhs) noexcept
1310   { return __lhs.compare(__rhs) < 0; }
1311 
1312   inline bool operator==(const path& __lhs, const path& __rhs) noexcept
1313   { return __lhs.compare(__rhs) == 0; }
