1766     // _S_negate {{{2
1767     template <typename _Tp, size_t _Np>
1768       _GLIBCXX_SIMD_INTRINSIC static constexpr _MaskMember<_Tp>
1769       _S_negate(_SimdWrapper<_Tp, _Np> __x) noexcept
1770       { return !__x._M_data; }
