1247   hypot(const simd<_Tp, _Abi>& __x, const simd<_Tp, _Abi>& __y,
1248     const simd<_Tp, _Abi>& __z)
1249   {
1250     return __hypot<conditional_t<__is_fixed_size_abi_v<_Abi>,
1251                  const simd<_Tp, _Abi>&, simd<_Tp, _Abi>>>(__x,
1252                                        __y,
1253                                        __z);
