2893           constexpr auto __one = __vector_broadcast<_Np, _Tp>(1);
2894           return __vector_bitcast<_Tp>(
2895             __vector_bitcast<_Tp>(
2896               (__xx & __signmask) |
2897               __vector_bitcast<_I>(__one)) // -1 or 1
2898             != __one);
2899         }
2900         }
2901     }*/
2902       }
