3309         else if constexpr (sizeof(__xi) == 16 && sizeof(_Tp) == 4)
3310           return _mm_mask_cmp_ps_mask(__k1, __xi, __yi, _CMP_LT_OQ);
3311         else if constexpr (sizeof(__xi) == 16 && sizeof(_Tp) == 8)
