3321           return __to_masktype(_mm256_cmp_pd(__xi, __yi, _CMP_LT_OQ));
3322         else if constexpr (sizeof(__xi) == 16 && sizeof(_Tp) == 4)
3323           return __auto_bitcast(_mm_cmp_ps(__xi, __yi, _CMP_LT_OQ));
