41 #ifdef PB_DS_CLASS_C_DEC
42 
43 PB_DS_CLASS_T_DEC
44 inline void
45 PB_DS_CLASS_C_DEC::
46 actual_erase_node(node_pointer p_z)
47 {
48   _GLIBCXX_DEBUG_ASSERT(m_size > 0);
49   --m_size;
50   _GLIBCXX_DEBUG_ONLY(debug_base::erase_existing(PB_DS_V2F(p_z->m_value));)
51   p_z->~node();
52   s_node_allocator.deallocate(p_z, 1);
53 }
54 
55 PB_DS_CLASS_T_DEC
56 inline void
57 PB_DS_CLASS_C_DEC::
58 update_min_max_for_erased_node(node_pointer p_z)
59 {
60   if (m_size == 1)
61     {
62       m_p_head->m_p_left = m_p_head->m_p_right = m_p_head;
63       return;
64     }
65 
66   if (m_p_head->m_p_left == p_z)
67     {
68       iterator it(p_z);
69       ++it;
70       m_p_head->m_p_left = it.m_p_nd;
71     }
72   else if (m_p_head->m_p_right == p_z)
73     {
74       iterator it(p_z);
75       --it;
76       m_p_head->m_p_right = it.m_p_nd;
77     }
78 }
79 
80 PB_DS_CLASS_T_DEC
81 void
82 PB_DS_CLASS_C_DEC::
83 clear()
84 {
85   PB_DS_STRUCT_ONLY_ASSERT_VALID((*this))
86   clear_imp(m_p_head->m_p_parent);
87   m_size = 0;
88   initialize();
89   _GLIBCXX_DEBUG_ONLY(debug_base::clear();)
90   PB_DS_STRUCT_ONLY_ASSERT_VALID((*this))
91 }
92 
93 PB_DS_CLASS_T_DEC
94 void
95 PB_DS_CLASS_C_DEC::
96 clear_imp(node_pointer p_nd)
97 {
98   if (p_nd == 0)
99     return;
100 
101   clear_imp(p_nd->m_p_left);
102   clear_imp(p_nd->m_p_right);
103   p_nd->~node();
104   s_node_allocator.deallocate(p_nd, 1);
105 }
106 #endif
