51 PB_DS_CLASS_T_DEC
52 PB_DS_CLASS_C_DEC::
53 left_child_next_sibling_heap() :
54   m_p_root(0),
55   m_size(0)
56 {
57   PB_DS_ASSERT_VALID((*this))
58 }
59 
60 PB_DS_CLASS_T_DEC
61 PB_DS_CLASS_C_DEC::
62 left_child_next_sibling_heap(const Cmp_Fn& r_cmp_fn) :
63   Cmp_Fn(r_cmp_fn),
64   m_p_root(0),
65   m_size(0)
66 {
67   PB_DS_ASSERT_VALID((*this))
68 }
69 
70 PB_DS_CLASS_T_DEC
71 PB_DS_CLASS_C_DEC::
72 left_child_next_sibling_heap(const PB_DS_CLASS_C_DEC& other) 
73 : Cmp_Fn(other), m_p_root(0), m_size(0)
74 {
75   m_size = other.m_size;
76   PB_DS_ASSERT_VALID(other)
77   m_p_root = recursive_copy_node(other.m_p_root);
78   m_size = other.m_size;
79   PB_DS_ASSERT_VALID((*this))
80 }
81 
82 PB_DS_CLASS_T_DEC
83 void
84 PB_DS_CLASS_C_DEC::
85 swap(PB_DS_CLASS_C_DEC& other)
86 {
87   PB_DS_ASSERT_VALID((*this))
88   PB_DS_ASSERT_VALID(other)
89   value_swap(other);
90   std::swap((Cmp_Fn& )(*this), (Cmp_Fn& )other);
91   PB_DS_ASSERT_VALID((*this))
92   PB_DS_ASSERT_VALID(other)
93 }
94 
95 PB_DS_CLASS_T_DEC
96 void
97 PB_DS_CLASS_C_DEC::
98 value_swap(PB_DS_CLASS_C_DEC& other)
99 {
100   std::swap(m_p_root, other.m_p_root);
101   std::swap(m_size, other.m_size);
102 }
103 
104 PB_DS_CLASS_T_DEC
105 PB_DS_CLASS_C_DEC::
106 ~left_child_next_sibling_heap()
107 {
108   clear();
109 }
