468 #if __cplusplus >= 201103L
470       void
471       shrink_to_fit() noexcept
472       {
473     if (capacity() > size())
474       {
475         __try
476           { this->reserve(0); }
477         __catch(...)
478           { }
479       }
480       }
481 #endif
