33 #ifndef _GLIBCXX_PARALLEL_SEARCH_H
34 #define _GLIBCXX_PARALLEL_SEARCH_H 1
35 
36 #include <bits/stl_algobase.h>
37 
38 #include <parallel/parallel.h>
39 #include <parallel/equally_split.h>
