1473     {
1474       if ((__token != "/dev/urandom" && __token != "/dev/random")
1475       || !(_M_file = std::fopen(__token.c_str(), "rb")))
1476     std::__throw_runtime_error(__N("random_device::"
1477                        "random_device(const std::string&)"));
