175 struct stbrp_context
176 {
177    int width;
178    int height;
179    int align;
180    int init_mode;
181    int heuristic;
182    int num_nodes;
183    stbrp_node *active_head;
184    stbrp_node *free_head;
185    stbrp_node extra[2]; // we allocate two extra nodes so optimal user-node-count is 'width' not 'width+2'
186 };
