133 #define IS_SPACE(x) (((x) == ' ') || ((x) == '\t'))
134 #define IS_DIGIT(x) ((unsigned int)((x) - '0') < (unsigned int)(10))
135 #define IS_NEW_LINE(x) (((x) == '\r') || ((x) == '\n') || ((x) == '\0'))
