187 /* http://stackoverflow.com/questions/5710091/how-does-atoi-function-in-c-work
188 */
189 static int my_atoi(const char *c) {
190   int value = 0;
191   int sign = 1;
192   if (*c == '+' || *c == '-') {
193     if (*c == '-') sign = -1;
194     c++;
195   }
196   while (((*c) >= '0') && ((*c) <= '9')) { /* isdigit(*c) */
197     value *= 10;
198     value += (int)(*c - '0');
199     c++;
200   }
201   return value * sign;
202 }
