204 /* Make index zero-base, and also support relative index. */
205 static int fixIndex(int idx, unsigned int n) {
206   if (idx > 0) return idx - 1;
207   if (idx == 0) return 0;
208   return (int)n + idx; /* negative value = relative */
209 }
