432 static float parseFloat(const char **token) {
433   const char *end;
434   double val = 0.0;
435   float f = 0.0f;
436   skip_space(token);
437   end = (*token) + until_space((*token));
438   val = 0.0;
439   tryParseDouble((*token), end, &val);
440   f = (float)(val);
441   (*token) = end;
442   return f;
443 }
