271 // Rotate Vector by float in Degrees.
272 RMDEF Vector2 Vector2Rotate(Vector2 v, float degs)
273 {
274     float rads = degs*DEG2RAD;
275     Vector2 result = {v.x * cosf(rads) - v.y * sinf(rads) , v.x * sinf(rads) + v.y * cosf(rads) };
276     return result;
277 }
