300     GLM_FUNC_QUALIFIER int16 bitfieldInterleave(int8 x, int8 y)
301     {
302         union sign8
303         {
304             int8 i;
305             uint8 u;
306         } sign_x, sign_y;
307 
308         union sign16
309         {
310             int16 i;
311             uint16 u;
312         } result;
313 
314         sign_x.i = x;
315         sign_y.i = y;
316         result.u = bitfieldInterleave(sign_x.u, sign_y.u);
317 
318         return result.i;
319     }
