127     inline void
128     __thread_yield() noexcept
129     {
130 #if defined _GLIBCXX_HAS_GTHREADS && defined _GLIBCXX_USE_SCHED_YIELD
131      __gthread_yield();
132 #endif
133     }
134 
135     inline void
136     __thread_relax() noexcept
137     {
138 #if defined __i386__ || defined __x86_64__
139       __builtin_ia32_pause();
140 #else
141       __thread_yield();
142 #endif
143     }
