374     template<typename _Rep, typename _Period>
375       constexpr
376       enable_if_t<numeric_limits<_Rep>::is_signed, duration<_Rep, _Period>>
377       abs(duration<_Rep, _Period> __d)
378       {
379     if (__d >= __d.zero())
380       return __d;
381     return -__d;
382       }
