727     typedef duration<_Rep1, _Period1>           __dur1;
728     typedef duration<_Rep2, _Period2>           __dur2;
729     typedef typename common_type<__dur1,__dur2>::type   __cd;
730     return __cd(__cd(__lhs).count() % __cd(__rhs).count());
731       }
