1465         && __builtin_strcmp(__s, "POSIX") != 0)
1466       {
1467         __c_locale __tmp;
1468         this->_S_create_c_locale(__tmp, __s);
1469         this->_M_initialize_moneypunct(__tmp);
1470         this->_S_destroy_c_locale(__tmp);
1471       }
1472       }
