138     template<typename _Tp>
139       concept __adl_end = __class_or_enum<remove_reference_t<_Tp>>
140     && requires(_Tp& __t)
141     {
142       { __decay_copy(end(__t)) } -> sentinel_for<__range_iter_t<_Tp>>;
143     };
