543     requires destructible<range_value_t<_Range>>
544     constexpr borrowed_iterator_t<_Range>
545     __destroy_fn::operator()(_Range&& __r) const noexcept
546     {
547       return (*this)(ranges::begin(__r), ranges::end(__r));
548     }
