2154   template<typename _Bi_iter, class _Alloc>
2155     inline bool
2156     operator!=(const match_results<_Bi_iter, _Alloc>& __m1,
2157            const match_results<_Bi_iter, _Alloc>& __m2)
2158     { return !(__m1 == __m2); }
2159 #endif
