2984       // [28.12.2.2.4]
2985       void
2986       _M_normalize_result()
2987       {
2988     if (_M_position != _Position())
2989       _M_result = &_M_current_match();
2990     else if (_M_has_m1)
2991       _M_result = &_M_suffix;
2992     else
2993       _M_result = nullptr;
2994       }
