304   template <typename _Tp>
305     _GLIBCXX_SIMD_INTRINSIC
306     static constexpr _Tp _S_bit_shift_right(_Tp __x, int __y)
307     { return static_cast<_Tp>(__promote_preserving_unsigned(__x) >> __y); }
