759   template <typename _Tp, typename _Abi>
760     _GLIBCXX_SIMD_INTRINSIC constexpr static bool
761     _S_none_of(simd_mask<_Tp, _Abi> __k)
762     { return !__k._M_data; }
763 
764   // }}}
