485     inline void 
486     __bit_allocate(std::size_t* __pbmap, std::size_t __pos) throw()
487     {
488       std::size_t __mask = 1 << __pos;
489       __mask = ~__mask;
490       *__pbmap &= __mask;
491     }
492   
496     inline void 
497     __bit_free(std::size_t* __pbmap, std::size_t __pos) throw()
498     {
499       std::size_t __mask = 1 << __pos;
500       *__pbmap |= __mask;
501     }
