183 PB_DS_CLASS_T_DEC
184 bool
185 PB_DS_CLASS_C_DEC::
186 erase(key_const_reference r_key)
187 {
188   point_iterator it = find(r_key);
189   if (it == end())
190     return false;
191   erase(it);
192   return true;
193 }
