432       template<typename Ptr>
433       inline static Ptr
434       mid_pointer(Ptr p_begin, Ptr p_end)
435       {
436     _GLIBCXX_DEBUG_ASSERT(p_end >= p_begin);
437     return (p_begin + (p_end - p_begin) / 2);
438       }
