142 PB_DS_CLASS_T_DEC
143 void
144 PB_DS_CLASS_C_DEC::
145 initialize()
146 {
147   new (m_p_head) head();
148   m_p_head->m_p_parent = 0;
149   m_p_head->m_p_min = m_p_head;
150   m_p_head->m_p_max = m_p_head;
151   m_size = 0;
152 }
