154 PB_DS_CLASS_T_DEC
155 template<typename It>
156 void
157 PB_DS_CLASS_C_DEC::
158 copy_from_range(It first_it, It last_it)
159 {
160   while (first_it != last_it)
161     insert(*(first_it++));
162 }
