43 PB_DS_CLASS_T_DEC
44 inline typename PB_DS_CLASS_C_DEC::iterator
45 PB_DS_CLASS_C_DEC::
46 find_by_order(size_type order)
47 {
48   if (empty())
49     return end();
50 
51   ++order;
52   node_iterator nd_it = node_begin();
53 
54   while (true)
55     {
56       if (order > nd_it.get_metadata())
57     return ++base_type::rightmost_it(nd_it);
58 
59       const size_type num_children = nd_it.num_children();
60       if (num_children == 0)
61     return *nd_it;
62 
63       for (size_type i = 0; i < num_children; ++i)
64     {
65       node_iterator child_nd_it = nd_it.get_child(i);
66       if (order <= child_nd_it.get_metadata())
67         {
68           i = num_children;
69           nd_it = child_nd_it;
70         }
71       else
72         order -= child_nd_it.get_metadata();
73     }
74     }
75 }
76 
77 PB_DS_CLASS_T_DEC
78 inline typename PB_DS_CLASS_C_DEC::const_iterator
79 PB_DS_CLASS_C_DEC::
80 find_by_order(size_type order) const
81 { return const_cast<PB_DS_CLASS_C_DEC*>(this)->find_by_order(order); }
